mapper.update:
update user set user_id = #{user_id}
				,password = #{password}
				,nick_name = #{nick_name}
				,name = #{name}
				,email = #{email}
				,phone = #{phone}
				,address1 = #{address1}
				,address2 = #{address2}
				,zipcode = #{zipcode}
				,user_images = #{user_images}
where user_seq=#{user_seq}
